
public class Main {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int hap;
		hap = plus(100, 200);
		System.out.printf("100과 200의 plus( ) 메소드 결과는 : %d\n", hap);
	}

	private static int plus(int i, int j) {
		// TODO Auto-generated method stub
		int result;
		result = i + j;
		return result;
	}

}
